import "./user.css"
import { CalendarToday, LocationSearching, MailOutline, PermIdentity, PhoneAndroid, Publish } from "@mui/icons-material" 
import { Link } from "react-router-dom"
import { useLocation } from 'react-router-dom';
import { useEffect, useState } from "react";
import userCRUDService from "../../services/UserService";
import { DateTime } from "luxon";


// A custom hook that builds on useLocation to parse
// the query string for you.
function useQuery() {
    console.log(useLocation());
    return new URLSearchParams(useLocation().search);
}

export default function User() {
    let query = useQuery();
    // const getUserbyId = window.location.pathname.slice(6,7);
    // console.log(getUserbyId);

    // var currentURL = useLocation();
    // var getUserId = new URLSearchParams(currentURL);
    // var currentUserId= getUserId.get("id"); 
    // console.log(query.get("id"));
    // console.log(query.toString());
    // console.log(currentURL);

    const [user,setUser] = useState(
        {
            id: 0,
            firstName: "",
            lastName: "",
            address: "",
            email: "",
            avatar: ""
          }
    );

    this.handleInputChange = this.handleInputChange.bind(this);

    handleInputChange((event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
        [name]: value
        });
    });

    useEffect(() => {
        // userCRUDService.getUsers().then((users) => {
        //   setData(users);
        // });
        async function fetchData() {
          const request = await userCRUDService.getUserById(query.get("id"));
          setUser(request.data);
          console.log(request);
      }
      fetchData();

      }, []);

      var userBirthday = DateTime.fromISO(user.birthday).toLocaleString();
    //   var userBirthday = DateTime.fromISO(user.birthday, {zone: 'utc'});
      console.log(userBirthday);


      const handleUpdate = (id) => {
        setUser(user.filter((item)=>item.id !== id));
        userCRUDService.updateUsers(id);
      };

    //   const userUpdate = {
    //     id: user.id,
    //     firstName: document.getElementByClassName('userShowUsername').value,
    //     lastName: document.getElementByClassName('userShowUsername').value,
    //     adress: document.getElementByClassName('userShowUsername').value,
    //     email: document.getElementByClassName('userShowUsername').value,
    //     avatar: document.getElementByClassName('userShowUsername').value,
    // }


    return (
        <div className="user">
            <div className="userTitleContainer">
                <h1 className="userTitle">Edit User</h1>
                <Link to ="/newUser">
                <button className="userAddButton">Create</button>
                </Link>
            </div>
            <div className="userContainer">
                <div className="userShow">
                    <div className="userShowTop">
                        <img src={user.avatar} alt="" className="userShowImg" />
                        <div className="userShowTopTitle">
                            <span className="userShowUsername">{user.firstName} {user.lastName}</span>
                            <span className="userShowUserTitle">{user.jobTitle}</span>
                        </div>
                    </div>
                    <div className="userShowBottom">
                        <span className="userShowTitle">Account Details</span>

                        <div className="userShowInfo">
                            <PermIdentity className="userShowIcon"/>
                            <span className="userShowInfoTitle">{user.username}</span>
                        </div>
                        <div className="userShowInfo">
                            <CalendarToday className="userShowIcon"/>
                            <span className="userShowInfoTitle">{userBirthday}</span>
                        </div>
                        <span className="userShowTitle">Contact Details</span>
                        <div className="userShowInfo">
                            <PhoneAndroid className="userShowIcon"/>
                            <span className="userShowInfoTitle">{user.phoneNumber}</span>
                        </div>
                        <div className="userShowInfo">
                            <MailOutline className="userShowIcon"/>
                            <span className="userShowInfoTitle">{user.email}</span>
                        </div>
                        <div className="userShowInfo">
                            <LocationSearching className="userShowIcon"/>
                            <span className="userShowInfoTitle">{user.address}</span>
                        </div>
                    </div>
                </div>
                <div className="userUpdate">
                    <span className="userUpdateTitle">Edit</span>
                    <form className="userUpdateForm">
                        <div className="userUpdateLeft">
                            <div className="userUpdateItem">
                                <label>Username</label>
                                <input type="text" placeholder={user.username} className="userUpdateInput" value={user.username}/>
                            </div>
                            <div className="userUpdateItem">
                                <label>First Name</label>
                                <input type="text" placeholder= {user.firstName} className="userUpdateInput"/>
                            </div>
                            <div className="userUpdateItem">
                                <label>Last Name</label>
                                <input type="text" placeholder= {user.lastName} className="userUpdateInput"/>
                            </div>
                            <div className="userUpdateItem">
                                <label>Job Title</label>
                                <input type="text" placeholder={user.jobTitle} className="userUpdateInput"/>
                            </div>
                            <div className="userUpdateItem">
                                <label>Email</label>
                                <input type="email" placeholder={user.email} className="userUpdateInput"/>
                            </div>
                            <div className="userUpdateItem">
                                <label>Phone</label>
                                <input type="tel" placeholder={user.phoneNumber} className="userUpdateInput"/>
                            </div>
                            <div className="userUpdateItem">
                                <label>Adress</label>
                                <input type="text" placeholder={user.address} className="userUpdateInput"/>
                            </div>
                            <div className="userUpdateItem">
                                <label>Birthday</label>
                                <input type="text" placeholder={userBirthday} className="userUpdateInput"/>
                            </div>
                        </div>
                        <div className="userUpdateRight">
                            <div className="userUpdateUpload">
                                <img className="userUpdateImg" src={user.avatar} alt="" />
                                <label htmlFor="file"><Publish className="userUpdateIcon"/></label>
                                <input type="file" id="file" style={{ display: "none" }}/>
                            </div>
                            <button className="userUpdateButton">Update</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    )
}
