import "./user.css"
import { CalendarToday, LocationSearching, MailOutline, PermIdentity, PhoneAndroid, Publish } from "@mui/icons-material" 
import { Link } from "react-router-dom"
import { useLocation } from 'react-router-dom';
import { useEffect, useState } from "react";
import userCRUDService from "../../services/UserService";
import Moment from 'react-moment';

// A custom hook that builds on useLocation to parse
// the query string for you.
function useQuery() {
    console.log(useLocation());
    return new URLSearchParams(useLocation().search);
}

export default function User() {
    let query = useQuery();
    // const getUserbyId = window.location.pathname.slice(6,7);
    // console.log(getUserbyId);

    // var currentURL = useLocation();
    // var getUserId = new URLSearchParams(currentURL);
    // var currentUserId= getUserId.get("id"); 
    // console.log(query.get("id"));
    // console.log(query.toString());
    // console.log(currentURL);

    const [user,setUser] = useState(
        {
            id: 0,
            firstName: "string",
            lastName: "string",
            address: "string",
            email: "string",
            avatar: "string"
          }
    );

    useEffect(() => {
        // userCRUDService.getUsers().then((users) => {
        //   setData(users);
        // });
        async function fetchData() {
          const request = await userCRUDService.getUserById(query.get("id"));
          setUser(request.data);
          console.log(request);
      }
      fetchData();

      }, []);


    return (
        <div className="user">
            <div className="userTitleContainer">
                <h1 className="userTitle">Edit User</h1>
                <Link to ="/newUser">
                <button className="userAddButton">Create</button>
                </Link>
            </div>
            <div className="userContainer">
                <div className="userShow">
                    <div className="userShowTop">
                        <img src="https://unsplash.com/photos/ZHvM3XIOHoE/download?force=true&w=640" alt="" className="userShowImg" />
                        <div className="userShowTopTitle">
                            <span className="userShowUsername">{user.firstName} {user.lastName}</span>
                            <span className="userShowUserTitle">Software Engineer</span>
                        </div>
                    </div>
                    <div className="userShowBottom">
                        <span className="userShowTitle">Account Details</span>

                        <div className="userShowInfo">
                            <PermIdentity className="userShowIcon"/>
                            <span className="userShowInfoTitle">{user.username}</span>
                        </div>
                        <div className="userShowInfo">
                            <CalendarToday className="userShowIcon"/>
                            <span className="userShowInfoTitle"><Moment date={user.birthday} format="DD.MM.YYYY" ></Moment></span>
                        </div>
                        <span className="userShowTitle">Contact Details</span>
                        <div className="userShowInfo">
                            <PhoneAndroid className="userShowIcon"/>
                            <span className="userShowInfoTitle">017661422822</span>
                        </div>
                        <div className="userShowInfo">
                            <MailOutline className="userShowIcon"/>
                            <span className="userShowInfoTitle">{user.email}</span>
                        </div>
                        <div className="userShowInfo">
                            <LocationSearching className="userShowIcon"/>
                            <span className="userShowInfoTitle">{user.address}</span>
                        </div>
                    </div>
                </div>
                <div className="userUpdate">
                    <span className="userUpdateTitle">Edit</span>
                    <form className="userUpdateForm">
                        <div className="userUpdateLeft">
                            <div className="userUpdateItem">
                                <label>Username</label>
                                <input type="" placeholder='${user.}' className="userUpdateInput"/>
                            </div>
                            <div className="userUpdateItem">
                                <label>Full Name</label>
                                <input type="" placeholder="Peter Becker" className="userUpdateInput"/>
                            </div>
                            <div className="userUpdateItem">
                                <label>Email</label>
                                <input type="" placeholder="peterbeck99@gmail.com" className="userUpdateInput"/>
                            </div>
                            <div className="userUpdateItem">
                                <label>Phone</label>
                                <input type="" placeholder="017661422822" className="userUpdateInput"/>
                            </div>
                            <div className="userUpdateItem">
                                <label>Adress</label>
                                <input type="" placeholder="Berlin | Deutschland" className="userUpdateInput"/>
                            </div>
                        </div>
                        <div className="userUpdateRight">
                            <div className="userUpdateUpload">
                                <img className="userUpdateImg" src="https://unsplash.com/photos/ZHvM3XIOHoE/download?force=true&w=640" alt="" />
                                <label htmlFor="file"><Publish className="userUpdateIcon"/></label>
                                <input type="file" id="file" style={{ display: "none" }}/>
                            </div>
                            <button className="userUpdateButton">Update</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    )
}
